import useApiRequest from "../hooks/useApiRequest";
import Search from '../components/Search/Search'
import Card from "../components/Card/Card";
import Loading from "../components/Loading/Loading";
import { useParams } from "react-router-dom";
import { useEffect } from "react";

const SearchResults = () => {

    const { isLoading, error, pokemons } = useApiRequest('https://pokeapi.co/api/v2/pokemon?limit=1118')
    const { term } = useParams();

    useEffect(() => {
        console.log(pokemons)
    })

    return ( 
        <div>
            <Search/>
            { term === undefined? <p>Oops...</p> :

            <div className="card-list">
                {isLoading && <Loading/>}
                {pokemons && pokemons.filter((val) => {
                if (term === null) {
                    return val
                } else if (val.name.includes(term)) {
                    return val
                }
                    return false
                }).map(item => <Card itemURL ={item.url} key={item.url}/>)}
            </div>}
        </div>
    );
}

export default SearchResults;
