import { useEffect, useRef } from 'react';
import { NavLink } from 'react-router-dom';
import './Search.css'

const Search = ({ searchTerm, setSearchTerm, setUrl, url, link }) => {

    const value = useRef(null)

    const handleClick = () => {
        setSearchTerm(value.current)
        setUrl(url)
    }

    useEffect(() => {

    })

    return ( 
        <div role="search" className='search'>
            <input aria-label="search term" placeholder='Search...' onChange={(event) => value.current = event.target.value}/>
            <NavLink to={link + value.current}><button aria-label="search" onClick={() => handleClick()}>Search</button></NavLink>
        </div>
    );
}

export default Search;
