import React, { useEffect, useState } from "react";
import useApiRequest from '../hooks/useApiRequest';
import CardList from "../components/CardList/CardList";
import Loading from "../components/Loading/Loading";
import Error from "../components/Error/Error";
import Button from "../components/Button/Button";

const Home = () => {

    const [url, setUrl] = useState(`https://pokeapi.co/api/v2/pokemon?limit=35`)
    const { next, isLoading, error, pokemons, loadPokemons } = useApiRequest(url)

    useEffect(() => {
        document.title = 'Pokedéx'

        window.addEventListener('scroll', handleScroll, {
            passive: true
        })
        return () => {
            window.removeEventListener('scroll', handleScroll);
        }
    }, [])

    const loadMore = () => {
        setUrl(next);
    }

    const handleScroll = () => {

        const bottom = Math.ceil(window.innerHeight + window.scrollY) >= document.documentElement.scrollHeight

        if (bottom) {
          console.log('at the bottom');
          loadMore();
        }
    };

    if(error) {
        return (<Error errorMessage={error} handleError={loadPokemons}/>)
    }

    return ( 
        <div className='Home'>
            {isLoading && <Loading/>}
            <CardList data={pokemons} />
            {/* {pokemons && <Button buttonText={!isLoading? 'Load more Pokémon' : 'Loading...'} handleClick={loadMore} />} */}
        </div>
    );
}

export default Home;
