import React, { useEffect, useState } from "react";
import List from "../components/List/List";
import Loading from "../components/Loading/Loading";
import Error from "../components/Error/Error";
import Button from "../components/Button/Button";

const Home = () => {

    const [isLoading, setIsLoading] = useState(null)
    const [error, setError] = useState(null)
    const [pokemons, setPokemons] = useState([])
    const [offSet, setOffSet] = useState(0)

    const loadPokemons = () => {
        fetch(`https://pokeapi.co/api/v2/pokkemon?limit=20&offset=${offSet}`)
        .then(response => response.json())
        .then(PokemonData => {
            if(offSet === 0) {
                setPokemons(PokemonData.results)
            } else {
                setPokemons([...pokemons, ...PokemonData.results])
            }
        })
        .catch(error => {
            console.log(error)
            setError('Error on load.')
        })
        .finally(() => {
            setIsLoading(false)
        })
    }

    const loadMore = () => {
        setOffSet(offSet + 20)
    }

    useEffect(() => {
        setIsLoading(true)
        setError(null)

        loadPokemons()
    }, [offSet])


    if(error) {
        return (<Error errorMessage={error} handleError={loadPokemons}/>)
    }

    return ( 
        <React.Fragment>
            {isLoading && <Loading/>}
            <List data={pokemons}/>
            {isLoading || <Button buttonText={'Load more.'} handleClick={loadMore}/>}
        </React.Fragment>
    );
}

export default Home;
