import useApiRequest from "../hooks/useApiRequest";
import Card from "../components/Card/Card";
import Loading from "../components/Loading/Loading";
import context from "../utils/context"
import { useContext, useEffect } from "react";
import { useParams } from "react-router-dom";

const SearchResults = () => {

    const { isLoading, error, pokemons } = useApiRequest('https://pokeapi.co/api/v2/pokemon?limit=1118')
    const { searchTerm, setSearchTerm } = useContext(context)


    useEffect()

    return ( 
        <div>
            { searchTerm === null? <p>404</p> :
            <div className="card-list">
                {isLoading && <Loading/>}
                {pokemons && pokemons.filter((val) => {
                if (searchTerm === null) {
                    return val
                } else if (val.name.includes(searchTerm)) {
                    return val
                }
                    return false
                }).map(item => <Card itemURL ={item.url} key={item.url}/>)}
            </div>}
        </div>
    );
}

export default SearchResults;
