    const { order } = useParams()
    const url = `https://pokeapi.co/api/v2/pokemon/${order}`
    const { itemData, isLoading } = useDetailsRequest(url)

    return ( 
        <div className='item-details'>
            <div>
                <img />
            </div>
        </div>
    );
}

export default Pokemon;
