import useApiRequest from "../hooks/useApiRequest"

const Item = ({itemURL}) => {

    const { itemData, isLoading } = useApiRequest(itemURL)

    return (
        <div className="list">
            <div className="list-item">
                <div className="item-image">
                    <img src={itemData.img} alt=""/>
                </div>
                <div className="item-name">
                    <h1>{itemData.name}</h1>
                </div>
            </div>
        </div>
    );
}

export default Item;
