import { useEffect, useState } from "react";
import ListItem from "../ListItem/ListItem";

const List = ({itemURL}) => {

    const [pokemonDetail, setPokemonDetail] = useState([])

    const loadPokemonsDetails = (url) => {
        fetch()
        .then(response => response.json())
        .then(data => {
            return {
                'name': data.name,
                'img' : data.sprites.front_default
            }
        })
        .then(data => {
            setPokemonDetail(data)
        })
    }

    useEffect(() => {
        loadPokemonsDetails()
    }, [])

    return (
        <div className="list">
            <ListItem itemName={pokemonDetail.name} itemIMG={pokemonDetail.img}/>
        </div>
    );
}

export default List;
