import React, { useEffect } from "react";
import useApiRequest from '../hooks/useApiRequest';
import CardList from "../components/CardList/CardList";
import Loading from "../components/Loading/Loading";
import Error from "../components/Error/Error";
import Button from "../components/Button/Button";

const Main = () => {

    const url = 'https://pokeapi.co/api/v2/pokemon?limit=56'
    const { next, isLoading, error, pokemons, loadPokemons, isMounted } = useApiRequest(url)

    useEffect(() => {
        document.title = 'Pokedéx'
    }, [])

    if(error) {
        return (<Error errorMessage={error} handleError={loadPokemons}/>)
    }

    return ( 
        <div className='Home'>
            {isLoading && <Loading/>}
            <CardList data={pokemons}/>
            {next && <Button buttonText={!isLoading? 'Load more Pokémon' : 'Loading...'} handleClick={() => loadPokemons(isMounted ,next)} />}
        </div>
    );
}

export default Main;
