import React, { useState } from "react";
import useApiRequest from '../hooks/useApiRequest';
import CardList from "../components/CardList/CardList";
import Loading from "../components/Loading/Loading";
import Error from "../components/Error/Error";
import Button from "../components/Button/Button";
import Search from "../components/Search/Search";

const Home = () => {

    const {next, isLoading, error, pokemons, loadPokemons, searchTerm, setSearchTerm } = useApiRequest(url)
    const [url, setUrl] = useState(`https://pokeapi.co/api/v2/pokemon?limit=${searchTerm === null? 153 : 1118}`)


    const loadMore = () => {
        setUrl(next)
    }

    if(error) {
        return (<Error errorMessage={error} handleError={loadPokemons}/>)
    }

    return ( 
        <div className='Home'>
            {isLoading && <Loading/>}
            {isLoading || <Search searchTerm={searchTerm} setSearchTerm={setSearchTerm}/>}
            <CardList data={pokemons} searchTerm={searchTerm}/>
            {/* {next && <Button buttonText={'Load more.'} handleClick={loadMore} />} */}
        </div>
    );
}

export default Home;
