import { useEffect, useRef, useState } from 'react';
import { NavLink } from 'react-router-dom';
import './Search.css'

const Search = ({ searchTerm, setSearchTerm, handleSearch, cancelSearchUrl, searchUrl, setUrl }) => {

    const value = useRef(null)

    const handleClick = () => {
        if(value.current) {
            setSearchTerm(value.current)
            setUrl(searchUrl)
        }
    }

    return ( 
        <div>
            {searchTerm && <button onClick={() => handleSearch(cancelSearchUrl)}>x</button>}
            <div role="search" className='search'>
                <input aria-label="search term" placeholder='Search...' onChange={(event) => {value.current = event.target.value }}/>
                <button aria-label="search" onClick={() => handleClick()}>Search</button>
            </div>
        </div>
    );
}

export default Search;
