import { useState, useEffect } from "react"
import capitalizeFirstLetter from "../utils/helpers"

const useApiRequest = url => {

    const [next, setNext] = useState(null)
    const [isLoading, setIsLoading] = useState(true)
    const [error, setError] = useState(null)
    const [pokemons, setPokemons] = useState([])

    const abortController = new AbortController()
    let isCancelled = false

    const loadPokemons = () => {
        if(error) {
            setError(null)
            setIsLoading(true)
        }
        fetch(url, { signal: AbortController.signal })
        .then(response => response.json())
        .then(dataSet => {
            setNext(dataSet.next)
            Promise.all(dataSet.results.map(result => 
                fetch(result.url)
                .then(response => response.json())
                .then(data => {
                    return {
                        'name': capitalizeFirstLetter(data.name),
                        'img' : data.sprites.other['official-artwork'].front_default,
                        'id': data.id,
                        'types' : data.types,
                        'weight': 'Weight: ' + data.weight/10 + ' kg',
                        'height': 'Height: ' + data.height/10 + ' m',
                        'ability': 'Ability: ' + capitalizeFirstLetter(data.abilities[0].ability.name),
                        'stats': data.stats
                    }
                })
            ))
            .then(data => {
                if(!isCancelled) {
                    setPokemons()
                }
            })
        })
        .catch(error => {
            if(!isCancelled) {
                console.log(error)
                setError('Error on load.')
            }
        })
        .finally(() => {
            if(!isCancelled) {
                setIsLoading(false)
            }
        })
    }

    useEffect(() => {
        setIsLoading(true)
        loadPokemons()
        return () => {
            abortController.abort()
            isCancelled = true
        }
    }, [url])

    return { next, isLoading, error, pokemons, loadPokemons }

}

export default useApiRequest;
