import { useState, useEffect, useCallback , useRef } from "react"
import { useParams } from "react-router-dom"
import capitalizeFirstLetter from "../utils/helpers"

const useApiRequest = url => {

    const [next, setNext] = useState(null)
    const [isLoading, setIsLoading] = useState(true)
    const [error, setError] = useState(null)
    const [pokemons, setPokemons] = useState([])

    const { term } = useParams()

    const abortController =  new AbortController()
    const previous = useRef(null)
    const isMounted = useRef(null)

    const loadPokemons = useCallback((isMounted, url) => {
        previous.current = null
        setIsLoading(true)
        if(error) {
            setError(null)
        }
        fetch(url, { signal: AbortController.signal })
        .then(response => response.json())
        .then(dataSet => {
            if(isMounted) {
                setNext(dataSet.next)
            }
            Promise.all(dataSet.results.filter(pokemon => {
                if(!term) {
                    return pokemon
                } else if (pokemon.name.includes(term)) {
                    return pokemon
                }
                return false
            }).map(pokemon => 
                fetch(pokemon.url)
                .then(response => response.json())
                .then(data => {
                    return {
                        'link': '/pokemon/' + data.id ,
                        'name': capitalizeFirstLetter(data.name),
                        'img' : data.sprites.other['official-artwork'].front_default,
                        'id': data.id
                    }
                })
            ))
            .then(data => {
                if(isMounted) {
                    if(!previous.current) {
                        setPokemons(data)
                        previous.current = true
                    } else {
                        setPokemons([...pokemons, ...data])
                    }
                }
            })
        })
        .catch(error => {
            if(isMounted) {
                console.log(error)
                setError('Error on load.')
            }
        })
        .finally(() => {
            if(isMounted) {
                setTimeout(() => {

                }, timeout);
                setIsLoading(true)       
            }
        })
    }, [error, pokemons, term])

    useEffect(() => {
        isMounted.current = true
        setIsLoading(true)
        loadPokemons(isMounted, url)
        return () => {
            abortController.abort()
            isMounted.current = false
        }
    }, [term])

    return { next, isLoading, error, pokemons, loadPokemons, isMounted }

}

export default useApiRequest;
